# 目標函數 
def target(inputs: dict) -> dict:
    """
    目標函數 - 包裝 RAG 系統以便評估
    
    這是被評估的函數，接收問題並返回系統生成的回答。
    """
    global travel_rag

    query = inputs["query"]
    print(f"處理問題: {query}")
    result = travel_rag.process_query(query)

    if isinstance(result, dict):
        answer = result.get("answer", str(result))
    else:
        answer = str(result)
    
    return {"response": answer}

#  評估器函數 
class OpenEndedGrade(BaseModel):
    """評估分數的模型定義"""
    relevance_score: float = Field(
        description="Score between 0 and 0.25 indicating relevance to the query area and topic"
    )
    applicability_score: float = Field(
        description="Score between 0 and 0.25 indicating how suitable the recommendations are for the specific need"
    )
    diversity_score: float = Field(
        description="Score between 0 and 0.25 indicating variety of options provided"
    )
    accuracy_score: float = Field(
        description="Score between 0 and 0.25 indicating factual accuracy of descriptions"
    )
    total_score: float = Field(
        description="Sum of all scores, between 0 and 1"
    )
    reasoning: str = Field(
        description="Explanation for the scores given"
    )

